<?php
// Ваш Base64 из поля "file" (сокращён для примера)
$base64String = ""; // замените на полный Base64

// Декодируем Base64
$decodedData = base64_decode($base64String);

if ($decodedData === false) {
    die("Ошибка декодирования Base64");
}

// Сохраняем в файл PNG
$filePath = 'sticker.png';
file_put_contents($filePath, $decodedData);

echo "Стикер сохранён в файл: $filePath\n";
?>
